#!/usr/bin/env node
"use strict";var i=require("commander");var n="0.1.0";var t=s=>{console.log(s)};var e=new i.Command;e.name("Sample ENV Generator (.env.sample)").description("A CLI tool for generating a sample env(.env.sample) file from a .env file").version(n);e.command("gen-env").description("Generate Sample ENV(.env.sample) from .env file.").option("-n, --name <name>","Name for the sample env file",".env.sample").option("-v, --values <placeholder>","Default Placeholder Value","YOUR_FIELD_VALUE").action(t);e.parse();
